import React, { Fragment, Component } from "react";
import { getRoutes, addRoute, addMultipleRoutes } from "../../services/agent";
import { Card, Spinner, Table } from "reactstrap";
import toast from "react-hot-toast";
import { statusCode } from "../../utility/constants/utilObject";
import { toastStyle } from "../../utility/helper";
import { Edit3, Trash2 } from "react-feather";
import "./index.css";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import CustomInputBox from "../../component/CustomInputBox";
import BarLoader from "react-spinners/BarLoader";
import Pagination from "react-js-pagination";
import Excel from "exceljs";
import Papa from "papaparse";

const override = {
  borderColor: "#1761fd",
  width: "100%",
};

class RouteRecords extends Component {
  constructor(props) {
    super(props);
    this.state = {
      routeList: [],
      userListOrg: [],
      loader: false,
      searchFilter: "",
      selectedUserForDocument: null,
      showModePopup: false,
      type: "",
      status: "",
      email: "",
      filterByText: "",
      viewTokenDetails: [],
      tokenDetails: [],
      loading: [],
      updateToken: [],
      months: [],
      credits: [],
      updating: [],
      activePage: 1,
      totalItemsCount: 0,
      pageRangeDisplayed: 10,
      itemsCountPerPage: 100,
      newRouteInfo: {
        intermediateStops: [],
        startTime: "",
      },
      showDeletePopup: false,
    };
  }

  handleInputChange = (event) => {
    const { value } = event.target;
    this.setState({
      newRouteInfo: {
        ...this.state.newRouteInfo,
        startTime: value,
      },
    });
  };

  componentDidMount = () => {
    localStorage.removeItem("active_doc");
    this.getRoutes();
  };
  handleAddIntermediateStop = () => {
    this.setState((prevState) => ({
      newRouteInfo: {
        ...prevState.newRouteInfo,
        intermediateStops: [
          ...prevState.newRouteInfo.intermediateStops,
          {
            stopName: "",
            arrivalTime: "",
            departureTime: "",
            frequency: "",
            stopLocation: "",
          },
        ],
      },
    }));
  };
  handleIntermediateStopChange = (e, index, fieldName) => {
    const { value } = e.target;
    this.setState((prevState) => {
      const intermediateStops = [...prevState.newRouteInfo.intermediateStops];
      intermediateStops[index] = {
        ...intermediateStops[index],
        [fieldName]: value,
      };
      return {
        newRouteInfo: {
          ...prevState.newRouteInfo,
          intermediateStops,
        },
      };
    });
  };

  handlePageChange(pageNumber) {
    console.log(`active page is ${pageNumber}`);
    this.setState({ activePage: pageNumber }, () => {});
  }

  changeTab = (index) => {
    this.setState({ selectedTab: index });
  };

  handleSearch = (e) => {
    this.setState({ searchFilter: e.target.value });
  };

  getRoutes = () => {
    this.setState({ loader: true });
    const queryParams = `?page=${this.state.activePage}&records=${this.state.itemsCountPerPage}`;
    getRoutes(queryParams)
      .then((res) => {
        if (res.status == statusCode.HTTP_200_OK) {
          let routes = res.data.data;
          this.setState({
            totalItemsCount: routes.count,
            routeList: routes.rows,
            loader: false,
          });
        } else {
          toast.error(res.message, { ...toastStyle.error });
          this.setState({ loader: false, vehicleList: [], userListOrg: [] });
        }
      })
      .catch((err) => {
        toast.error(err?.message, { ...toastStyle.error });
        this.setState({ loader: false, vehicleList: [], userListOrg: [] });
      });
  };

  addRoute = (isActive = true) => {
    let newRouteInfo = this.state.newRouteInfo;
    if (
      !newRouteInfo.number ||
      !newRouteInfo.startPoint ||
      !newRouteInfo.endPoint
    ) {
      toast.error("Please fill all the mandatory fields!!");
      return;
    }

    this.setState({ loader: true });
    let payload = {
      routeNo: newRouteInfo.number,
      startPoint: newRouteInfo.startPoint,
      endPoint: newRouteInfo.endPoint,
      depotname: newRouteInfo.depotname,
      startTime: formatTime(newRouteInfo.startTime), // Use formatted start time
      endTime: formatTime(newRouteInfo.endTime),
      frequency: newRouteInfo.frequency, // Use formatted end time
      intermediateStops: newRouteInfo?.intermediateStops || "",
    };

    function formatTime(time) {
      const formattedTime = new Date(time)
        .toLocaleString("en-US", {
          year: "numeric",
          month: "2-digit",
          day: "2-digit",
          hour: "2-digit",
          minute: "2-digit",
          second: "2-digit",
        })
        .replace(/(\d+)\/(\d+)\/(\d+), (\d+):(\d+):(\d+)/, "$3-$2-$1 $4:$5:$6");
      return formattedTime;
    }

    if (newRouteInfo.id) {
      payload = {
        ...payload,
        routeId: newRouteInfo.id,
        isActive: isActive,
      };
    }

    addRoute(payload)
      .then((res) => {
        if (res.status == statusCode.HTTP_200_OK) {
          let routes = res.data.data;
          this.setState({
            totalItemsCount: routes.count,
            routeList: routes.rows,
            loader: false,
            showModePopup: false,
            showDeletePopup: false,
            newRouteInfo: {},
          });
        } else {
          toast.error(res.message, { ...toastStyle.error });
          this.setState({ loader: false, newRouteInfo: {}, userListOrg: [] });
        }
      })
      .catch((err) => {
        toast.error(err?.message, { ...toastStyle.error });
        this.setState({ loader: false, userListOrg: [] });
      });
  };

  addRoutes = (payload) => {
    addMultipleRoutes({ routes: payload })
      .then((res) => {
        if (res.status == statusCode.HTTP_200_OK) {
          let routes = res.data.data;
          this.setState({
            totalItemsCount: routes.count,
            routeList: routes.rows,
            loader: false,
            showModePopup: false,
            showDeletePopup: false,
          });
          toast.success("Records added successfully!!");
        } else {
          toast.error(res.message, { ...toastStyle.error });
          this.setState({ loader: false, newRouteInfo: {}, userListOrg: [] });
        }
      })
      .catch((err) => {
        toast.error(err?.message, { ...toastStyle.error });
        this.setState({ loader: false, userListOrg: [] });
      });
  };

  showPopup = (email, status = "", type) => {
    this.setState({
      showModePopup: true,
      email: email,
      status: status,
      type: type,
    });
  };

  hidePopup = () => {
    this.setState({
      showModePopup: false,
      email: "",
      status: "",
      type: "",
      showDeletePopup: false,
      newRouteInfo: {},
    });
  };

  onChangeRouteNo = (text) => {
    // let regex = new RegExp(/^(?=.*[a-zA-Z])(?=.*[0-9])[A-Za-z0-9]+$/);
    if (text.length > 6) {
      toast.error("Only 6 digit route number is allowed");
      return;
    }
    let regex = new RegExp("^[a-zA-Z0-9]+$");
    if (regex.test(text) || text == "") {
      this.setState({
        newRouteInfo: {
          ...this.state.newRouteInfo,
          number: text.toUpperCase(),
        },
      });
    }
  };

  renderModePopup = () => {
    if (this.state.showModePopup) {
      return (
        <Modal
          size="lg"
          isOpen={this.state.showModePopup}
          style={{ marginTop: "5%" }}
        >
          <ModalHeader>Add Route </ModalHeader>
          <ModalBody>
            <div className="row">
              <div className="col-lg-6">
                <CustomInputBox
                  label="Route Number"
                  //smallBoxEnabled={true}
                  mandatory={true}
                  //info={"Capture what you want this blog to achieve or for what target audience is this being written for. <br/>For example: to encourage working professionals to try meditation as a tool for stress relief, <br/> to promote organic farming, to motivate parents to take mental health seriously."}
                  onChange={(text) => {
                    this.onChangeRouteNo(text);
                  }}
                  value={this.state.newRouteInfo?.number || ""}
                  charCount={false}
                  size={"md"}
                  placeholderText="Input the route number"
                  maxLength={300}
                  //note="Provide information about the blog's goal or objective"
                />

                <CustomInputBox
                  label="Start Point"
                  mandatory={true}
                  onChange={(text) => {
                    this.setState({
                      newRouteInfo: {
                        ...this.state.newRouteInfo,
                        startPoint: text,
                      },
                    });
                  }}
                  value={this.state.newRouteInfo?.startPoint || ""}
                  size={"md"}
                  placeholderText="Input the start point"
                />
                <CustomInputBox
                  label="End Point"
                  mandatory={true}
                  size={"md"}
                  placeholderText="Input the end point"
                  onChange={(text) => {
                    this.setState({
                      newRouteInfo: {
                        ...this.state.newRouteInfo,
                        endPoint: text,
                      },
                    });
                  }}
                  value={this.state.newRouteInfo?.endPoint || ""}
                />
              </div>

              <div className="col-lg-6">
                <CustomInputBox
                  label="Depotname"
                  mandatory={true}
                  size={"md"}
                  placeholderText="Enter depot name"
                  onChange={(text) => {
                    this.setState({
                      newRouteInfo: {
                        ...this.state.newRouteInfo,
                        depotname: text,
                      },
                    });
                  }}
                  value={this.state.newRouteInfo?.depotname || ""}
                />
              </div>
              <div className="col-lg-4">
                {/* <CustomInputBox
                  label="Start Time"
                  mandatory={false}
                  size={"md"}
                  inputType="time"
                  placeholderText="Enter Start Time"
                  onChange={(text) => {
                    this.setState({
                      newRouteInfo: {
                        ...this.state.newRouteInfo,
                        startTime: text,
                      },
                    });
                  }}
                  value={this.state.newRouteInfo?.startTime || ""}
                /> */}
                <div>
                  <label htmlFor="start-time">Start Time</label>
                  <input
                    type="datetime-local"
                    id="start-time"
                    placeholder="Enter Start Time"
                    value={this.state.newRouteInfo.startTime}
                    onChange={this.handleInputChange}
                    size="md"
                  />
                        
                </div>
              </div>
              <div className="col-lg-4">
                <CustomInputBox
                  label="End Time"
                  mandatory={false}
                  size={"md"}
                  inputType="time"
                  placeholderText="Enter End Time"
                  onChange={(text) => {
                    this.setState({
                      newRouteInfo: {
                        ...this.state.newRouteInfo,
                        endTime: text,
                      },
                    });
                  }}
                  value={this.state.newRouteInfo?.endTime || ""}
                />
              </div>
              <div className="col-lg-4">
                <CustomInputBox
                  label="Frequency"
                  mandatory={false}
                  size={"md"}
                  placeholderText="Enter Frequency"
                  onChange={(text) => {
                    this.setState({
                      newRouteInfo: {
                        ...this.state.newRouteInfo,
                        frequency: text,
                      },
                    });
                  }}
                  value={this.state.newRouteInfo?.frequency || ""}
                />
              </div>
              <div className="col-lg-6">
                <div className="form-group">
                  <h3>Add Intermediate Stops</h3>
                  {this.state.newRouteInfo.intermediateStops.map(
                    (stop, index) => (
                      <div key={index}>
                        <input
                          type="text"
                          name="stopName"
                          placeholder="Stop Name"
                          value={stop.stopName}
                          onChange={(e) =>
                            this.handleIntermediateStopChange(
                              e,
                              index,
                              "stopName"
                            )
                          }
                        />
                        <input
                          type="text"
                          name="arrivalTime"
                          placeholder="Arrival Time"
                          value={stop.arrivalTime}
                          onChange={(e) =>
                            this.handleIntermediateStopChange(
                              e,
                              index,
                              "arrivalTime"
                            )
                          }
                        />
                        <input
                          type="text"
                          name="departureTime"
                          placeholder="Departure Time"
                          value={stop.departureTime}
                          onChange={(e) =>
                            this.handleIntermediateStopChange(
                              e,
                              index,
                              "departureTime"
                            )
                          }
                        />
                        <input
                          type="text"
                          name="frequency"
                          placeholder="Frequency"
                          value={stop.frequency}
                          onChange={(e) =>
                            this.handleIntermediateStopChange(
                              e,
                              index,
                              "frequency"
                            )
                          }
                        />
                        <input
                          type="text"
                          name="stopLocation"
                          placeholder="Stop Location"
                          value={stop.stopLocation}
                          onChange={(e) =>
                            this.handleIntermediateStopChange(
                              e,
                              index,
                              "stopLocation"
                            )
                          }
                        />
