import React, { Component } from "react";
import { getRoutes, addRoute, addMultipleRoutes } from "../../services/agent";
import {
  Card,
  Spinner,
  Table,
  Button,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
} from "reactstrap";
import toast from "react-hot-toast";
import { statusCode } from "../../utility/constants/utilObject";
import { toastStyle } from "../../utility/helper";
import { Edit3, Trash2 } from "react-feather";
import "./index.css";
import CustomInputBox from "../../component/CustomInputBox";
import BarLoader from "react-spinners/BarLoader";
import Pagination from "react-js-pagination";
import Excel from "exceljs";
import Papa from "papaparse";

const override = {
  borderColor: "#1761fd",
  width: "100%",
};

class RouteRecords extends Component {
  state = {
    routeList: [],
    loader: false,
    showModePopup: false,
    showDeletePopup: false,
    newRouteInfo: { intermediateStops: [] },
    activePage: 1,
    totalItemsCount: 0,
    itemsCountPerPage: 100,
  };

  componentDidMount() {
    localStorage.removeItem("active_doc");
    this.getRoutes();
  }

  handlePageChange = (pageNumber) => {
    this.setState({ activePage: pageNumber }, this.getRoutes);
  };

  getRoutes = () => {
    this.setState({ loader: true });
    const queryParams = `?page=${this.state.activePage}&records=${this.state.itemsCountPerPage}`;
    getRoutes(queryParams)
      .then((res) => {
        if (res.status === statusCode.HTTP_200_OK) {
          const routes = res.data.data;
          this.setState({
            totalItemsCount: routes.count,
            routeList: routes.rows,
            loader: false,
          });
        } else {
          toast.error(res.message, { ...toastStyle.error });
          this.setState({ loader: false });
        }
      })
      .catch((err) => {
        toast.error(err?.message, { ...toastStyle.error });
        this.setState({ loader: false });
      });
  };

  handleAddIntermediateStop = () => {
    this.setState((prevState) => ({
      newRouteInfo: {
        ...prevState.newRouteInfo,
        intermediateStops: [
          ...prevState.newRouteInfo.intermediateStops,
          {
            stopName: "",
            arrivalTime: "",
            departureTime: "",
            frequency: "",
            stopLocation: "",
          },
        ],
      },
    }));
  };

  handleIntermediateStopChange = (e, index, fieldName) => {
    const { value } = e.target;
    this.setState((prevState) => {
      const intermediateStops = [...prevState.newRouteInfo.intermediateStops];
      intermediateStops[index][fieldName] = value;
      return { newRouteInfo: { ...prevState.newRouteInfo, intermediateStops } };
    });
  };

  handleRouteChange = (field, value) => {
    this.setState((prevState) => ({
      newRouteInfo: { ...prevState.newRouteInfo, [field]: value },
    }));
  };

  handleAddRoute = () => {
    const { newRouteInfo } = this.state;
    if (
      !newRouteInfo.number ||
      !newRouteInfo.startPoint ||
      !newRouteInfo.endPoint
    ) {
      toast.error("Please fill all the mandatory fields!!");
      return;
    }

    const payload = {
      routeNo: newRouteInfo.number,
      startPoint: newRouteInfo.startPoint,
      endPoint: newRouteInfo.endPoint,
      depotname: newRouteInfo.depotname,
      startTime: formatTime(newRouteInfo.startTime),
      endTime: formatTime(newRouteInfo.endTime),
      frequency: newRouteInfo.frequency,
      intermediateStops: newRouteInfo.intermediateStops || "",
      routeId: newRouteInfo.id,
    };

    addRoute(payload)
      .then((res) => {
        if (res.status === statusCode.HTTP_200_OK) {
          const routes = res.data.data;
          this.setState({
            totalItemsCount: routes.count,
            routeList: routes.rows,
            loader: false,
            showModePopup: false,
            showDeletePopup: false,
            newRouteInfo: { intermediateStops: [] },
          });
        } else {
          toast.error(res.message, { ...toastStyle.error });
          this.setState({ loader: false });
        }
      })
      .catch((err) => {
        toast.error(err?.message, { ...toastStyle.error });
        this.setState({ loader: false });
      });
  };

  handleDeleteRoute = (route) => {
    this.setState({
      newRouteInfo: {
        number: route.routeNo,
        startPoint: route.startPoint,
        endPoint: route.endPoint,
        depotname: route.depotname,
        startTime: route.startTime,
        endTime: route.endTime,
        frequency: route.frequency,
        intermediateStops: route?.intermediateStops || "",
        id: route.routeId,
      },
      showDeletePopup: true,
    });
  };

  handleEditRoute = (route) => {
    this.setState({
      newRouteInfo: {
        number: route.routeNo,
        startPoint: route.startPoint,
        endPoint: route.endPoint,
        depotname: route.depotname,
        startTime: route.startTime,
        endTime: route.endTime,
        frequency: route.frequency,
        intermediateStops: route?.intermediateStops || "",
        id: route.routeId,
      },
      showModePopup: true,
    });
  };

  _handleFileLoad = async (e) => {
    const file = e.target.files[0];
    const extension = file.name.split(".").pop().toLowerCase();
    this.setState({ loader: true });
    let params = [];

    if (extension === "csv") {
      Papa.parse(file, {
        header: false,
        skipEmptyLines: true,
        complete: (results) => {
          params = [...results.data];
          params.splice(0, 1); // Remove header
          this.addRoutes(params);
        },
      });
    } else if (extension === "xlsx") {
      const wb = new Excel.Workbook();
      const reader = new FileReader();
      reader.readAsArrayBuffer(file);
      reader.onload = () => {
        const buffer = reader.result;
        wb.xlsx.load(buffer).then((workbook) => {
          workbook.eachSheet((sheet) => {
            sheet.eachRow((row, rowIndex) => {
              if (rowIndex !== 1) {
                const temp = [...row.values];
                temp.splice(0, 1); // Remove empty value from start
                params.push(temp);
              }
            });
            this.addRoutes(params);
          });
        });
      };
    } else {
      this.setState({ loader: false });
      toast.error("We are accepting only csv/xlsx file!");
    }
  };

  addRoutes = (routes) => {
    addMultipleRoutes({ routes })
      .then((res) => {
        if (res.status === statusCode.HTTP_200_OK) {
          const routes = res.data.data;
          this.setState({
            totalItemsCount: routes.count,
            routeList: routes.rows,
            loader: false,
            showModePopup: false,
            showDeletePopup: false,
          });
          toast.success("Records added successfully");
        } else {
          toast.error(res.message, { ...toastStyle.error });
          this.setState({ loader: false });
        }
      })
      .catch((err) => {
        toast.error(err?.message, { ...toastStyle.error });
        this.setState({ loader: false });
      });
  };

  render() {
    const { showModePopup, showDeletePopup, loader, newRouteInfo } = this.state;

    return (
      <Card className="route_record pr-4">
        {loader && (
          <div className="loader_style">
            <Spinner />
          </div>
        )}
        <div className="route_titile">
          <h3 className="title">Routes</h3>
          <Button
            className="title_button"
            onClick={() => this.setState({ showModePopup: true })}
          >
            Add
          </Button>
        </div>

        <div className="my-2 mb-4 w-25">
          <input
            type="file"
            name="file"
            className="custom-input"
            onChange={this._handleFileLoad}
          />
        </div>

        <RouteTable
          routeList={this.state.routeList}
          handleEditRoute={this.handleEditRoute}
          handleDeleteRoute={this.handleDeleteRoute}
        />

        <Pagination
          activePage={this.state.activePage}
          itemsCountPerPage={this.state.itemsCountPerPage}
          totalItemsCount={this.state.totalItemsCount}
          pageRangeDisplayed={5}
          onChange={this.handlePageChange}
        />

        <Modal
          isOpen={showModePopup}
          toggle={() => this.setState({ showModePopup: false })}
        >
          <ModalHeader toggle={() => this.setState({ showModePopup: false })}>
            Add Route
          </ModalHeader>
          <ModalBody>
            <RouteForm
              newRouteInfo={newRouteInfo}
              handleRouteChange={this.handleRouteChange}
              handleAddIntermediateStop={this.handleAddIntermediateStop}
              handleIntermediateStopChange={this.handleIntermediateStopChange}
            />
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.handleAddRoute}>
              Save
            </Button>
            <Button
              color="secondary"
              onClick={() => this.setState({ showModePopup: false })}
            >
              Cancel
            </Button>
          </ModalFooter>
        </Modal>

        <Modal
          isOpen={showDeletePopup}
          toggle={() => this.setState({ showDeletePopup: false })}
        >
          <ModalHeader toggle={() => this.setState({ showDeletePopup: false })}>
            Delete Route
          </ModalHeader>
          <ModalBody>
            <h5>Do you want to delete the route</h5>
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.handleAddRoute}>
              Delete
            </Button>
            <Button
              color="secondary"
              onClick={() => this.setState({ showDeletePopup: false })}
            >
              Cancel
            </Button>
          </ModalFooter>
        </Modal>
      </Card>
    );
  }
}

const RouteTable = ({ routeList, handleEditRoute, handleDeleteRoute }) => (
  <div className="custom-table">
    <Table className="route-records-table">
      <thead>
        <tr>
          <th>#</th>
          <th>Route</th>
          <th>Start Point</th>
          <th>End Point</th>
          <th>Depot Name</th>
          <th>Frequency</th>
          <th>Intermediate Stops</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {routeList.length > 0 ? (
          routeList.map((route, idx) => (
            <tr key={idx}>
              <td>{idx + 1}</td>
              <td>{route.routeNo}</td>
              <td>{route.startPoint}</td>
              <td>{route.endPoint}</td>
              <td>{route.depotname}</td>
              <td>{route.frequency}</td>
              <td>{route?.intermediateStops?.length || 0}</td>
              <td>
                <div className="action_container">
                  <div
                    className="edit_container"
                    onClick={() => handleEditRoute(route)}
                  >
                    <Edit3 className="edit_icon" />
                    <span className="icon_text">Edit</span>
                  </div>
                  <div
                    className="edit_container"
                    onClick={() => handleDeleteRoute(route)}
                  >
                    <Trash2 className="edit_icon" />
                    <span className="icon_text">Delete</span>
                  </div>
                </div>
              </td>
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan="8" className="nodatafound">
              No Route Records
            </td>
          </tr>
        )}
      </tbody>
    </Table>
  </div>
);

const RouteForm = ({
  newRouteInfo,
  handleRouteChange,
  handleAddIntermediateStop,
  handleIntermediateStopChange,
}) => (
  <form className="custom-input-form">
    <CustomInputBox
      value={newRouteInfo.number}
      placeholder="Enter route number"
      title="Route Number"
      handleChange={(e) => handleRouteChange("number", e.target.value)}
    />
    <CustomInputBox
      value={newRouteInfo.startPoint}
      placeholder="Enter start point"
      title="Start Point"
      handleChange={(e) => handleRouteChange("startPoint", e.target.value)}
    />
    <CustomInputBox
      value={newRouteInfo.endPoint}
      placeholder="Enter end point"
      title="End Point"
      handleChange={(e) => handleRouteChange("endPoint", e.target.value)}
    />
    <CustomInputBox
      value={newRouteInfo.depotname}
      placeholder="Enter depot name"
      title="Depot Name"
      handleChange={(e) => handleRouteChange("depotname", e.target.value)}
    />
    <CustomInputBox
      value={newRouteInfo.startTime}
      placeholder="Enter start time"
      title="Start Time"
      type="time"
      handleChange={(e) => handleRouteChange("startTime", e.target.value)}
    />
    <CustomInputBox
      value={newRouteInfo.endTime}
      placeholder="Enter end time"
      title="End Time"
      type="time"
      handleChange={(e) => handleRouteChange("endTime", e.target.value)}
    />
    <CustomInputBox
      value={newRouteInfo.frequency}
      placeholder="Enter frequency"
      title="Frequency"
      handleChange={(e) => handleRouteChange("frequency", e.target.value)}
    />
    <IntermediateStops
      intermediateStops={newRouteInfo.intermediateStops}
      handleAddIntermediateStop={handleAddIntermediateStop}
      handleIntermediateStopChange={handleIntermediateStopChange}
    />
  </form>
);

const IntermediateStops = ({
  intermediateStops,
  handleAddIntermediateStop,
  handleIntermediateStopChange,
}) => (
  <div>
    <div className="mt-4">
      <span className="header1">Intermediate Stops</span>
      <Button
        color="secondary"
        onClick={handleAddIntermediateStop}
        className="ml-2"
      >
        Add
      </Button>
    </div>
    {intermediateStops.map((stop, idx) => (
      <div key={idx} className="d-flex flex-wrap mt-2">
        <CustomInputBox
          value={stop.stopName}
          placeholder="Enter stop name"
          title="Stop Name"
          handleChange={(e) => handleIntermediateStopChange(e, idx, "stopName")}
        />
        <CustomInputBox
          value={stop.arrivalTime}
          placeholder="Enter arrival time"
          title="Arrival Time"
          type="time"
          handleChange={(e) =>
            handleIntermediateStopChange(e, idx, "arrivalTime")
          }
        />
        <CustomInputBox
          value={stop.departureTime}
          placeholder="Enter departure time"
          title="Departure Time"
          type="time"
          handleChange={(e) =>
            handleIntermediateStopChange(e, idx, "departureTime")
          }
        />
        <CustomInputBox
          value={stop.frequency}
          placeholder="Enter frequency"
          title="Frequency"
          handleChange={(e) =>
            handleIntermediateStopChange(e, idx, "frequency")
          }
        />
        <CustomInputBox
          value={stop.stopLocation}
          placeholder="Enter stop location"
          title="Stop Location"
          handleChange={(e) =>
            handleIntermediateStopChange(e, idx, "stopLocation")
          }
        />
      </div>
    ))}
  </div>
);

const formatTime = (time) => {
  return time?.split(":")?.join(":") ?? "";
};

export default RouteRecords;
